var deps = require('../lib/dependencies');

deps.analyze('./', {
  cache: true,
  latest: true,
  black_lists: ['black/']
}, function(err, version) {
  if (err) {
    console.log(err);
  } else {
    console.log('Packages version info: ');
    console.log(JSON.stringify(version, null, 4));    
  }
});
